mapper.insert:
INSERT INTO auctionbiding(auctionbidderno, auctionno, name)
VALUES (auctionbiding_seq.nextval, 
		#{auctionno}, 
	    #{name}
	   )

mapper.update:
UPDATE Auction
	SET
	  lastprice = #{lastprice},
	  name = #{name}
	WHERE auctionno = #{auctionno}

mapper.select:
SELECT COUNT(*)
 FROM AUCTIONBIDING
 WHERE auctionno = #{auctionno}
   AND name = #{name}

mapper.insert:
INSERT INTO auctionbidder(auctionbidderno, auctionno, name, bidprice)
VALUES (#{auctionbidderno}, 
		#{auctionno}, 
	    #{name},
	    #{bidprice}
	   )

mapper.update:
UPDATE Auction
	SET
	  lasttime = sysdate,
	  name = #{name}
	WHERE auctionno = #{auctionno}

mapper.select:
SELECT COUNT(*)
 FROM auctionbidder
 WHERE auctionno = #{auctionno}
   AND name = #{name}
